mapper.select:
select count(*)
from information_schema.TABLES
where table_name = #{tableName}

mapper.update:
CREATE TABLE ${tableName}
(
    `id`   int(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
    `meteorTime` Date  DEFAULT NULL COMMENT '时间',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2897 DEFAULT CHARSET=utf8 COMMENT='时间表';
